import ReactPlayer from 'react-player'
import Popup from "reactjs-popup";
import {IoMdClose} from 'react-icons/io'
import "reactjs-popup/dist/index.css";

import "./index.css";

const MovieItem = props => {
  const {itemDetails, selectButton} = props;
  
  const { thumbnailUrl,categoryId,id, videoUrl} = itemDetails;
 
 const clickIcon=()=>{
    selectButton(id)
 }

  return (
        <Popup
            modal
            trigger={
                <button  type="button" className="card-container" onClick={clickIcon} >
        <img style={{color: "#ffffff", width: "100%"}}src={thumbnailUrl} alt={categoryId} />
        </button>
            }
            className="popup-content"
            >
            {close => (
       <div className='over-container'>
           <button
           type="button"
           className="close-button"
           onClick={() => close()}
         >
           <IoMdClose size={20} />
         </button>
            <h1>{videoUrl}</h1>
       </div>
     )}
        </Popup>
      
        
    
    
  );
};

export default MovieItem;
