import { Component } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import MovieItem from "../MovieItem";
import "./index.css";

class MoviesSlider extends Component {
  state = { selectItem: "" };

  selectButton = (value) => {
    this.setState({ selectItem: value });
  };
  render() {
    const { itemList } = this.props;
    const { selectItem } = this.state;
    const videoElement=itemList.map(eachEl=>{
        if(eachEl.id===selectItem){
            return eachEl.videoUrl
        }
    }
        
    )
   
    
    const settings = {
      dots: false,
      speed: 500,
      slidesToShow: 4,
      slidesToScroll: 1,
    };

    return (
      <div className="slider-container">
        <Slider {...settings}>
          {itemList.map((eachValue) => (
            <MovieItem
              key={eachValue.id}
              itemDetails={eachValue}
              selectButton={this.selectButton}
              videoUrl={videoElement}
            />
          ))}
        </Slider>
      </div>
    );
  }
}

export default MoviesSlider;
