import Popup from "reactjs-popup";
import "reactjs-popup/dist/index.css";

import "./index.css";

const MovieItem = props => {
  const {itemDetails, selectButton,pop} = props;
  console.log(pop);
  const { thumbnailUrl,categoryId,id} = itemDetails;
 
 const clickIcon=()=>{
    selectButton(id)
 }

  return (
        <Popup
            modal
            trigger={
                <button  type="button" className="card-container" onClick={clickIcon} >
        <img style={{color: "#ffffff", width: "100%"}}src={thumbnailUrl} alt={categoryId} />
        </button>
            }
            className="popup-content"
            >
            {close => (
       <>
         <div>
           <p>React is a popular and widely used programming language</p>
         </div>
         <button
           type="button"
           className="trigger-button"
           onClick={() => close()}
         >
           Close
         </button>
       </>
     )}
        </Popup>
      
        
    
    
  );
};

export default MovieItem;
